window.onload=function(){var e=document.getElementById("canv").getContext("2d"),l=(e.font="50px Arial",e.fillStyle="#192632","Смотреть видео"),n=.6*Math.PI;e.translate(300,300),e.rotate(-1*n/2);for(let t=0;t<l.length;t++)e.rotate(n/l.length),e.save(),e.translate(0,-200),e.fillText(l[t],0,0),e.restore()};